mapper.insert:

	insert 
	into category(no, name, parent_no)
	values(nextval('seq_category_no'), #{ name }, #{ parentNo });

mapper.select:

	select * 
	from category
	order by no asc;

with recursive sub_category as ( select * from category where no = #{ value }
union all
select c.* from category as c join sub_category as sc on (c.parent_no = sc.no) ) select * from sub_category order by no;

select *
from category
where no = #{ value };

mapper.update:

	update category
	set
	name = #{ name },
	parent_no = #{ parentNo }
	where no = #{ no };

mapper.delete:

	delete
	from category
	where no = #{ value };
