mapper.insert:
insert into member(no, username, password, reg_date, name, birth_date, home_number, phone_number, email, status) values( nextval('seq_member_no'), '${ username }', encode(digest('${ password }', 'SHA512'), 'hex'), now(), encode(encrypt(convert_to('${ name }', 'utf-8'), 'key', 'AES'), 'hex'), encode(encrypt(convert_to('${ birthDate }', 'utf-8'), 'key', 'AES'), 'hex'),
encode(encrypt(convert_to('${ homeNumber }', 'utf-8'), 'key', 'AES'), 'hex'),
encode(encrypt(convert_to('${ phoneNumber }', 'utf-8'), 'key', 'AES'), 'hex'), encode(encrypt(convert_to('${ email }', 'utf-8'), 'key', 'AES'), 'hex'), '${ status.value }' );

mapper.select:

	select no, username, password, convert_from(decrypt(decode(name, 'hex'), 'key', 'AES'), 'utf-8')
	from member
	where username = #{ value };
