mapper.insert:
insert into member(no, username, password, reg_date, name, birth_date, home_number, phone_number, email, status, del_status) values( nextval('seq_member_no'), '${ username }', encode(digest('${ password }', 'SHA512'), 'hex'), now(), encode(encrypt(convert_to('${ name }', 'utf-8'), 'key', 'AES'), 'hex'), encode(encrypt(convert_to('${ birthDate }', 'utf-8'), 'key', 'AES'), 'hex'),
encode(encrypt(convert_to('${ homeNumber }', 'utf-8'), 'key', 'AES'), 'hex'),
encode(encrypt(convert_to('${ phoneNumber }', 'utf-8'), 'key', 'AES'), 'hex'), encode(encrypt(convert_to('${ email }', 'utf-8'), 'key', 'AES'), 'hex'), '${ status.value }', 'N' );

mapper.select:

	select no, username, password, convert_from(decrypt(decode(name, 'hex'), 'key', 'AES'), 'utf-8')
	from member
	where username = #{ value }
		and del_status = 'N';


	select no, username, convert_from(decrypt(decode(name, 'hex'), 'key', 'AES'), 'utf-8')
	from member
	where
		username = #{ username }
		and password = encode(digest(#{ password }, 'sha512'), 'hex')
		and del_status = 'N';

mapper.update:
update member set password = encode(digest('${ password }', 'SHA512'), 'hex'), name = encode(encrypt(convert_to('${ name }', 'utf-8'), 'key', 'AES'), 'hex'), birth_date = encode(encrypt(convert_to('${ birthDate }', 'utf-8'), 'key', 'AES'), 'hex'),
home_number = encode(encrypt(convert_to('${ homeNumber }', 'utf-8'), 'key', 'AES'), 'hex'),
phone_number = encode(encrypt(convert_to('${ phoneNumber }', 'utf-8'), 'key', 'AES'), 'hex'), email = encode(encrypt(convert_to('${ email }', 'utf-8'), 'key', 'AES'), 'hex') where no = #{ no };


	update member
	set del_status = 'Y'
	where no = #{ value };
