mapper.insert:
insert into orders(no, code, date, memo, status, orderer_name, orderer_home_number, orderer_phone_number, orderer_email, orderer_postal_code, orderer_base_address, orderer_detail_address, receiver_name, receiver_home_number, receiver_phone_number, receiver_postal_code, receiver_base_address, receiver_detail_address, total_order_account, member_status, member_no, password) values( nextval('seq_orders_no'), concat('P', to_char(now(), 'YYMMDD'), '-', lpad(to_hex(currval('seq_orders_no'))::varchar, 4, '0')), now(), #{ memo }, #{ status }, #{ ordererName }, #{ ordererHomeNumber }, #{ ordererPhoneNumber }, #{ ordererEmail }, #{ ordererPostalCode }, #{ ordererBaseAddress }, #{ ordererDetailAddress }, #{ receiverName }, #{ receiverHomeNumber }, #{ receiverPhoneNumber }, #{ receiverPostalCode }, #{ receiverBaseAddress }, #{ receiverDetailAddress }, null, #{ memberStatus },
#{ memberNo }
#{ password }
);

mapper.update:
update orders set total_order_account = ( select sum(oi.product_price) from orders_item oi where oi.order_no = currval('seq_orders_no') ) where no = currval('seq_orders_no')
update product_option_item set stock_quantity = case when stock_quantity is not null then stock_quantity - #{ item.quantity };
update product set stock_quantity = case when stock_quantity is not null then stock_quantity - #{ item.quantity };
