mapper.select:
select b.no as no, group_no as groupno, order_no as orderno, depth, title, b.user_no as userno,
date_format(b.reg_date, '%Y-%m-%d %H:%i:%s') as regdate, hit, u.name as username,
contents from board b, user u where b.user_no = u.no and b.no = #{ no };


	select count(*) 
	from board 
	where title like concat('%', #{ keyword }, '%');

mapper.update:

	update board 
	set order_no = order_no + 1 
	where group_no = #{ groupNo } 
		and order_no >= #{ orderNo } + 1;


	update board 
	set title = #{ title }, contents = #{ contents } 
	where no = #{ no };


	update board
	set hit = hit + 1
	where no = #{ no }


	update board 
	set status = 'DELETED' 
	where no = #{ no };
