mapper.insert:
insert into member(no, username, password, reg_date, name, birth_date, home_number, phone_number, email, status, role) values( nextval('seq_member_no'), #{ membervo.username }, encode(digest(#{ membervo.password }, 'SHA512'), 'hex'), now(), encode(encrypt(convert_to(#{ membervo.name }, 'utf-8'), 'key', 'AES'), 'hex'), encode(encrypt(convert_to(#{ membervo.birthDate }, 'utf-8'), 'key', 'AES'), 'hex'),
encode(encrypt(convert_to(#{ membervo.homeNumber }', 'utf-8'), 'key', 'AES'), 'hex'),
encode(encrypt(convert_to(#{ membervo.phoneNumber }, 'utf-8'), 'key', 'AES'), 'hex'), encode(encrypt(convert_to(#{ membervo.email }, 'utf-8'), 'key', 'AES'), 'hex'), 'ENABLE', 'USER' );
select currval('seq_member_no');

mapper.select:

	select username
	from member
	where username = #{ value };
