mapper.insert:

	insert 
	into user(no, name, email, password, gender, join_date) 
	values(null, #{ name }, #{ email }, #{ password }, #{ gender }, now());


	
		select last_insert_id();

mapper.select:

	select no, name 
	from user 
	where email = #{ email } 
		and password = #{ password };


	select no, name, email, gender 
	from user 
	where email = #{ value };


	select name, email, gender 
	from user 
	where no = #{ value };


	select name 
	from user 
	where no = #{ value };

mapper.update:

	update user
	set email = #{ email }, 


	
		name = #{ name }, 
	


	
		password = #{ password }, 
	


	gender = #{ gender }
	where no = #{ no };
